import { useState, useEffect } from "react";

export default function Article({ articles }) {
  const [authors, setAuthors] = useState([]);

  useEffect(() => {
    const author_url = "https://jsonplaceholder.typicode.com/users";

    async function getAuthors() {
      const resp = await fetch(author_url);
      const data = await resp.json();
      setAuthors(data);
      // console.log(data);
    }

    getAuthors();
  }, []);

  let userName = "";
  // let userWebsite = '';

  for (const prop in authors[0]) {
    // console.log(`${prop} = ${authors[0][prop]}`);
    if (`${prop}` === "name") {
      userName = `${authors[0][prop]}`;
    }
  }

  // console.log(authors[0]);

  return (
    <>
      <h3>{articles.title}</h3>
      {/* <a href={author[0].website}>{author[0].name}</a> */}
      {/* {author && (
        <p>{author.name}</p>
      )} */}
      <p>{userName}</p>
      <p>{articles.body}</p>
    </>
  );
}
